import './App.css';
import { useState } from 'react';
import eagleSound from './assets/eagle.mp3';
import useSound from 'use-sound';

function App() {
  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [playEagle] = useSound(eagleSound);
  const [eaglePic, setEaglePic] = useState(''); 

  function addPlayer(e) {

    players.push(currentPlayer);
    setCurrentPlayer('');
  }

  function doneAdding() {
    setGameStarted(true);
    showEagle();
  }

  function setPicture() {
    let min = Math.ceil(1);
    let max = Math.floor(8);
    let pic = Math.floor(Math.random() * (max - min + 1)) + min;
    console.log(pic);
    setEaglePic(`./assets/eagle${pic}`);
  }

  function showEagle() {

      var rand = Math.round(Math.random() * 100000);
      setTimeout(function() {
             playEagle();
             setPicture();

             setTimeout(function() {
                setEaglePic('');
             }, 3000)
             showEagle();  
      }, rand);
  }

  return (
    <div className="App">

      {!gameStarted ?
        <div>
          <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
            <div style={{ margin: '10px' }}>
              Player Name
            </div>
            <div style={{ margin: '10px' }}>
              <input style={{ width: "33%" }} name='playerName' type='text' value={currentPlayer} onChange={(e) => setCurrentPlayer(e.target.value)}></input>
            </div>
            <div style={{ margin: '10px' }}>
              <button onClick={addPlayer} >Add Player</button>
            </div>
            <div style={{ margin: '10px' }}>
              <button onClick={doneAdding} disabled={players.length < 2}>Done</button>
            </div>
          </div>



          <div style={{ display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
            <div>
              <ul style={{ listStyleType: 'none' }}>
                {
                  players.map(player => {
                    return <li>{player}</li>
                  })
                }
              </ul>
            </div>
          </div>
        </div>
        :
        <div>
          <image src={eaglePic}></image>
        </div>
      }
    </div>
  );
}

export default App;
