import './App.css';
import { useState } from 'react';

function App() {
  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [eagleSound, setEagleSound] = useState(new Audio('./assets/eagle.mp3'))

  function addPlayer(e) {

    players.push(currentPlayer);
    setCurrentPlayer('');
  }

  function doneAdding() {
    setGameStarted(true);
    showEagle();
  }

  function showEagle() {
    setInterval(function () {
      console.log('Start')
      eagleSound.play();
      console.log('Finsih')
    }, 60 * 100)
  }

  return (
    <div className="App">

      {!gameStarted ?
        <div>
          <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
            <div style={{ margin: '10px' }}>
              Player Name
            </div>
            <div style={{ margin: '10px' }}>
              <input style={{ width: "33%" }} name='playerName' type='text' value={currentPlayer} onChange={(e) => setCurrentPlayer(e.target.value)}></input>
            </div>
            <div style={{ margin: '10px' }}>
              <button onClick={addPlayer} >Add Player</button>
            </div>
            <div style={{ margin: '10px' }}>
              <button onClick={doneAdding}>Done</button>
            </div>
          </div>



          <div style={{ display: 'flex', justifyContent: 'center', flexDirection: 'row' }}>
            <div>
              <ul style={{ listStyleType: 'none' }}>
                {
                  players.map(player => {
                    return <li>{player}</li>
                  })
                }
              </ul>
            </div>
          </div>
        </div>
        :
        <div>

        </div>
      }
    </div>
  );
}

export default App;
