  const [players, setPlayers] = useState([]);
  const [currentPlayer, setCurrentPlayer] = useState('');


  function addPlayer(e) {

    players.push(currentPlayer);
    setCurrentPlayer('');
  }

  function doneAdding() {

  }

  return (
    <div className="App">
      <input name='playerName' type='text' value={currentPlayer} onChange={(e) => setCurrentPlayer(e.target.value)}></input>
      <button onClick={addPlayer} >Add Player</button>
      <button onClick={doneAdding}>Done</button>

      <div style={{ justifyContent: 'center' }}>
        <div style={{ width: '300px', justifyContent: 'center', alignSelf: 'center' }}>
          <ul >
            {
              players.map(player => {
                return <li>{player}</li>
              })
            }
          </ul>
        </div>

      </div>
    </div>
  );
}

export default App;
